def tratar_s03_1_estrutura_organizacional_2():
    """
    Função para tratar da questão '3.2.2. A Guarda Municipal faz o controle e fiscalização do trânsito?'
    """
    caminho_planilha = r'bases\S03_1_ESTRUTURA_ORGANIZACIONAL.xlsx'
    df = pd.read_excel(caminho_planilha)

    # Cria a nova coluna preenchida inicialmente com string vazia
    df["#ajust_3.2.2"] = ""

    # Analisar condições
    coluna_controle_transito = '3.2.2. A Guarda Municipal faz o controle e fiscalização do trânsito?'
    condicao1 = df['3.2. A Guarda Municipal possui unidades especializadas?'] == 'Não'
    condicao2 = df['3.2.1. Informe todos os tipos de unidades especializadas existentes: [Trânsito]'] == 'Não'
    df[coluna_controle_transito] = df[coluna_controle_transito].str.lower()
    condicao3 = df[coluna_controle_transito].str.contains(r'\bsim\b', regex=True, na=False)

    df.loc[condicao1 | condicao2, "#ajust_3.2.2"] = 'Não'
    df.loc[condicao3, "#ajust_3.2.2"] = 'Sim'
    df["#ajust_3.2.2"] = df["#ajust_3.2.2"].replace("", "Não informado")

    # Salvar planilha
    df.to_excel(caminho_planilha, index=False)


